#include "../includes/vm.h"
#include "../includes/op.h"
#include <unistd.h>

void	delete_split(char ***split)
{
	int		i;

	i = 0;
	while (*split[i] != NULL)
	{
		free(*split[i]);
		*split[i] = NULL;
		i++;
	}
	free(*split);
	*split = NULL;
}

byte	*get_map(t_players *players, int count)
{
	unsigned char	*map;
	int				i;
	int				j;
	char			*ptr;
	char			*tmp;

	j = 1;
	i = 0;
	map = (unsigned char*)malloc(sizeof(unsigned char) * MEM_SIZE);
	ptr = players[0].comands;
	while (i < MEM_SIZE && j <= count)
	{
		if (i >= (MEM_SIZE / count) * j)
		{
			ptr = players[j].comands;
			j++;
		}
		if (*ptr != '\0')
		{
			tmp = ft_strnew(2);
			map[i] = ft_atoi_base(ft_strncpy(tmp, ptr, 2), 16);
			ptr += 2;
			free(tmp);
		}
		else
			map[i] = 0;
		i++;
	}
	return (map);
}

void	get_command(t_players *players, byte *map)
{
	int i;

	i = 0;
	while (players[i].header.name != NULL)
	{
		if (players[i].stop == 0)
		{
			if (map[players[i].pos] == 1)
				live(players, &(map[players[i].pos + 1]), &(players[i]));
			else if (map[players[i].pos] == 2)
				ld(&(players[i]), map);
			else if (map[players[i].pos] == 3)
				st(&(players[i]), map);
			else if (map[players[i].pos] == 4)
				add(&(players[i]), map);
			else if (map[players[i].pos] == 5)
				sub(&(players[i]), map);
			else if (map[players[i].pos] == 6)
				and_cor(&(players[i]), map);
			else if (map[players[i].pos] == 9)
				zjmp(&(players[i]), map);
			else if (map[players[i].pos] == 11)
				sti(&(players[i]), map);
			else
			{
				players[i].pos += 1;
				players[i].pos = players[i].pos % MEM_SIZE;
			}
		}
		else
			players[i].stop -= 1;
		i++;
	}
	map[2] = map[2];
}

void	go_vm(t_players *players, int count)
{
	byte	*map;
	int		i;
	WINDOW	*win;
	WINDOW	*win1;
	WINDOW	*win2;

	i = 0;
	win1 = NULL;
	win2 = NULL;
	win = initscr();
	map = get_map(players, count);
	vizualize(map, players, &win1, win);
	status_bar(&win2, win, players);
	curs_set(0);
	getch();
	cursor_refresh(win1, win2, players, map);
	getch();
	while (1)
	{
		get_command(players, map);
		refresh_map(win1, map);
		cursor_refresh(win1, win2, players, map);
		// wrefresh(win1);
		// sleep(1);
	}
	delwin(win1);
	delwin(win2);
	delwin(win);
	endwin();
}

